mapper.select:
SELECT mem.mem_id, mem_pwd, mem_name, mem_phone, SUBSTR(mem_phone,1,3) as mobile1, SUBSTR(mem_phone,4,4) as mobile2, SUBSTR(mem_phone,8,4) as mobile3, mem_email, mem_birth, extract (year from mem_birth) as birth_year, extract (month from mem_birth) as birth_month, extract (day from mem_birth) as birth_day, mem_address, mem_postno, enabled, mem_joindate, updateDate, authority
FROM member mem LEFT OUTER JOIN authorities auth ON mem.mem_id = auth.mem_id
WHERE mem.mem_id = #{mem_id}

SELECT mem_id, mem_name
FROM MEMBER
WHERE mem_id = #{mem_id}

SELECT mem_id, mem_name, mem_email, mem_joindate
FROM member
WHERE mem_name = #{mem_name} AND mem_email = #{mem_email}

SELECT mem_id, mem_name, mem_email
FROM member
WHERE mem_id = ${mem_id} AND mem_name = ${mem_name} AND mem_email = ${mem_email}
